import {useState} from "react";
import "./UICrearColaborador.css";
import colaborador from "../modelos/colaborador.js";
import imgPerfil from "./user_photo.png";
import Dropdown from 'react-bootstrap/Dropdown';


import obtenerLideresEquipo from "../controladores/obtenerLideres";

export default function InterfazCrearColaborador() {
    

    const [nombre, setNombre] = useState('');
    const [nombreVacio, setNombreVacio] = useState(false);
    const [apellido, setApellido] = useState('');
    const [apellidoVacio, setapellidoVacio] = useState(false);
    const [correo, setCorreo] = useState('');
    const [correoVacio, setCorreoVacio] = useState(false);
    const [telefono, setTelefono] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');
    const [supervisadoPor, setSupervisadoPor] = useState('');
    const [definirSupervisado, setDefinirSupervisado] = useState(false);
    const [mostrarSupervisores, setMostrarSupervisores] = useState(false);
    const [idColaborador, setIdColaborador] = useState('');

    const [supervisores, setSupervisores] = useState(null);
    
    
    const Dropdown = ({ open, trigger, menu }) => {
        return (
          <div className="dropdown">
            {trigger}
            {open ? (
                <div>
              <ul className="menu">
                {menu.map((menuItem, index) => (
                  <li key={index} className="menu-item">{menuItem}</li>
                ))}
              </ul>
              </div>
            ) : null}
          </div>
        );
      };

    function crearColaborador(){
        if( nombre === '' || apellido === '' || correo === ''){
            if(nombre === ''){
                setNombreVacio(true);
            }
            if(apellido === ''){
                setapellidoVacio(true);
            }
            if(correo === ''){
                setCorreoVacio(true);
            }
            return;
        }
        console.log("Crear colaborador");
        let nuevoColaborador = new colaborador(
            nombre,
            apellido,
            correo,
            telefono,
            fechaNacimiento,
            supervisadoPor
        );
        console.log(nuevoColaborador);
        setDefinirSupervisado(true);
    }

    function FormularioCrearColaborador(){
        return(<><div className="form-group">
        <input style={{marginBottom: nombreVacio ? '0' : '2vh'}} type="text" name="nombre" placeholder="Nombre del empleado" 
        onChange={(e) => {
            setNombre(e.target.value);
            setNombreVacio(false);
        }}/>
        {nombreVacio ? <div><a>‚ùå El nombre no puede estar vacio</a></div> : null}
        <input style={{marginBottom: apellidoVacio ? '0' : '2vh'}} type="text" name="apellido" placeholder="Apellido del empleado"
        onChange={(e) => {
            setApellido(e.target.value);
            setapellidoVacio(false);
        }}/>
        {apellidoVacio ? <div><a>‚ùå El apellido no puede estar vacio</a></div> : null}
        <input style={{marginBottom: correoVacio ? '0' : '2vh'}} type="text" name="nombre" placeholder="Correo del empleado"
        onChange={(e) => {
            setCorreo(e.target.value);
            setCorreoVacio(false);
        }}/>
        {correoVacio ? <div><a>‚ùå El correo no puede estar vacio</a></div> : null}
        <input style={{marginBottom: '2vh'}} type="text" name="nombre" placeholder="Telefono del empleado"
        onChange={(e) => {
            setTelefono(e.target.value);
        }}/>
        <input style={{marginBottom: '2vh'}} type="text" name="nombre" placeholder="Fecha de nacimiento"
        onChange={(e) => {
            setFechaNacimiento(e.target.value);
        }}/>

        </div>
        <div className="form-btns">
        <button onClick={crearColaborador}>Crear üöÄ</button>
        <button type="reset">Limpiar üí¶</button>
        </div>
        </>);
    }

    function numeroRandom(min, max) {
        // üëáÔ∏è get number between min (inclusive) and max (inclusive)
        return Math.floor(Math.random() * (max - min + 1)) + min;
      }
    
      

    function renderSupervisores(){
        
        if(supervisores === null){
            
            return null;
        }
        console.log(supervisores);
        let lista = [];
        lista.push(<button onClick={() => {
            setSupervisadoPor('');
            setMostrarSupervisores(!mostrarSupervisores);
        }}>Sin supervisor</button>);

        for(let i = 0; i < supervisores.length; i++){
            lista.push(
                <button onClick={() => {
                    setSupervisadoPor(supervisores[i].firstname + ' ' + supervisores[i].lastname);
                    setMostrarSupervisores(!mostrarSupervisores);
                }}>{supervisores[i].firstname + ' ' + supervisores[i].lastname}</button>
                
            );
        }
        return lista;
      
    }

    function desplegarSupervisores(){
        fetch("/supervisor")
      .then((res) => setSupervisores(res.json()))
      
      
    }

    function PreviewColaborador(){
        
        if(idColaborador === ''){
            setIdColaborador(String(numeroRandom(100000, 999999)));
        }
        
        return(
            
                <div className="card-colaborador">
                    <div className="info-card">
                    <a>{nombre}</a>
                    <a>{apellido}</a>
                    <a>{correo}</a>
                    <a>{telefono}</a>
                    <a>{fechaNacimiento}</a>
                    </div>
                    <div className="data-card">
                        <div className="div-supervisado">
                            <a>Supervisado por</a>
                            <Dropdown
                            open={mostrarSupervisores}
                            trigger={<button className="trigger-btn-dropdown" onClick={() => {
                                setMostrarSupervisores(!mostrarSupervisores);
                                desplegarSupervisores();
                                
                            }}>{ supervisadoPor != '' ? supervisadoPor :'Elegir supervisor...'}</button>}
                            menu={renderSupervisores}
                            />
                        </div>
                        <div className="div-id">
                            <a>Id {idColaborador}</a>
                            </div>
                        
                    </div>
                </div>
            
       
        );
    }
    
    return (
        <div className="App-Crear-Colaborador">
            
            <a>Formulario para creacion de colaboradores üë®‚Äçüíº</a>
            {definirSupervisado ? PreviewColaborador() : FormularioCrearColaborador()}
            
        </div>
    );
}
