import {useState} from "react";
import "./UICrearColaborador.css";
import colaborador from "../modelos/colaborador.js";
import imgPerfil from "./user_photo.png";
import Dropdown from 'react-bootstrap/Dropdown';



export default function InterfazActualizarColaborador() {


    const [supervisor , setSupervisor] = useState(null);
    const [inputVacio , setInputVacio] = useState(true);
    const [buscador , setBuscador] = useState('');
    const [editarNombre, setEditarNombre] = useState(false);
    
    const Dropdown = ({ open, trigger, menu }) => {
        return (
          <div className="dropdown">
            {trigger}
            {open ? (
                <div>
              <ul className="menu">
                {menu.map((menuItem, index) => (
                  <li key={index} className="menu-item">{menuItem}</li>
                ))}
              </ul>
              </div>
            ) : null}
          </div>
        );
      };


    function parsearFecha(fecha){
        let fechaParseada = fecha.split('T');
        let fechaParseada2 = fechaParseada[0].split('-');
        return fechaParseada2[2]+'/'+fechaParseada2[1]+'/'+fechaParseada2[0];
    }

    function cardColaboradorEdit(id,nombre,apellido,correo,telefono,fecha,supervisado){
        return(<div className="card-colaborador">
        <div className="info-card">
        <input defaultValue={nombre}  ></input>
        <input defaultValue={apellido} ></input>
        <input defaultValue={correo} ></input>
        <input defaultValue={telefono} ></input>
        <input defaultValue={parsearFecha(fecha)} ></input>
        
        </div>
        <div className="data-card">
        <div className="div-supervisado">
                <a>Supervisado por</a>
                <Dropdown
                open={mostrarSupervisores}
                trigger={<button className="trigger-btn-dropdown" onClick={() => {
                    setMostrarSupervisores(!mostrarSupervisores);
                    
                   
                }}>{ supervisadoPor != '' ? supervisadoPor :'Elegir supervisor...'}</button>}
                menu={renderSupervisores()}
                />
            </div>
            <div className="div-id">
                <a>{'Identificador: '+id}</a>
                </div>
            
        </div>
    </div>);}

function renderSupervisores(){
        
    if(supervisores === null){
        console.log("supervisores es null");
        return null;
    }
    let lista = [];
    lista.push(<button onClick={() => {
        setSupervisadoPor('');
        setMostrarSupervisores(!mostrarSupervisores);
    }}>Sin supervisor</button>);

    for(let i = 0; i < supervisores.length; i++){
        lista.push(
            <button onClick={() => {
                setSupervisadoPor(supervisores[i].id);
                setMostrarSupervisores(!mostrarSupervisores);
            }}>{supervisores[i].firstname + ' ' + supervisores[i].lastname}</button>
            
        );
    }
    return lista;
}

async function desplegarSupervisores(){
    let consulta = await fetch ('/supervisor');
    
    let resultado = await consulta.json();
    setSupervisores(resultado);
}


    function buscarEmpleado(){
        fetch('/employee/'+buscador)
        .then(response => response.json())
        .then(data => {
            
            let lista = [];
            for(let i = 0; i < data.length; i++){
                lista.push(cardColaboradorEdit(data[i].id,data[i].firstname, data[i].lastname, data[i].email, data[i].phone, data[i].birthdate, data[i].supervisedby_id));
            }
            setSupervisor(lista);
            });
        
      }



    return (
        <div className="App-Crear-Colaborador">
            
            <a>Actualizar colaborador üë®‚Äçüíª</a>
            <div style={{marginTop: '5vh',marginBottom: '3vh'}}>
                <input onChange={(e)=>setBuscador(e.target.value)} className="input-search-supervisor" placeholder="Identificacion del supervisor..."></input>
            </div>
            {buscador === "" ? null : <button onClick={buscarEmpleado} className="btn-search-supervisor">Buscar</button>}
            <div style={{
                marginTop: '5vh',width: '100%', display: 'flex',
                justifyContent: 'center', alignItems: 'center'}}>{supervisor}</div>
        </div>
    );
}
