import {useState, useRef} from "react";
import "./UICrearColaborador.css";




export default function InterfazBuscarUF() {


    const [supervisor , setSupervisor] = useState(null);
    const [inputVacio , setInputVacio] = useState(true);
    const [day , setDay] = useState('');
    const [month , setMonth] = useState('');
    const [year , setYear] = useState('');


    function cardUF(id,uf,fecha){
        return(<div className="card-UF">
        <div className="info-card">
        <a>{id}</a>
        <a>{uf}</a>
        <a>{fecha}</a>

        </div>
        
    </div>);}

    //consultar UF en la API a la cual se accede asi: /uf/1-1-2023
    function buscarUF(){
        fetch('/uf/'+day+'-'+month+'-'+year)
        .then(res => res.json())
        .then(data => {
            console.log(data);
            setSupervisor(cardUF('123',data.mensaje.UF,data.mensaje.fecha));
        }
        );
    }

      const inputRefs = [
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        // Agrega tantas referencias como inputs desees controlar
      ];

      function handleInputChange(e, index) {
        const input = e.target;
        if (input.value.length >= input.maxLength && index < inputRefs.length - 1) {
            
          inputRefs[index + 1].current.focus();
        }
      }

    return (
        <div className="App-Buscar-UF">
            
            <a>Buscar valor de unidad de fomento por fecha ðŸ’¸</a>
            <div style={{marginTop: '5vh',marginBottom: '3vh',padding: '10px'}}>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[0]} onChange={
                                                (e) => 
                                                    {
                                                        handleInputChange(e, 0);
                                                        setDay(e.target.value);
                                                    }}></input>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[1]} onChange={
                                                (e) => 
                                                    {
                                                        handleInputChange(e, 1);
                                                        setMonth(e.target.value);
                                                    }}></input>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[2]} onChange={(e) => setYear('20'+e.target.value)}></input>
            </div>
            {year === "" ? null : <button onClick={buscarUF} className="btn-search-uf">Generar consulta en SII</button>}
            <div style={{
                marginTop: '5vh',width: '450px', display: 'flex',
                justifyContent: 'center', alignItems: 'center'}}>{supervisor}</div>
        </div>
    );
}
