import React, {useEffect} from "react";
import logo from "./logo.svg";
import InterfazCrearColaborador from "./vistas/UICrearColaborador";
import InterfazVerColaboradores from "./vistas/UIVerColaboradores";

import "./App.css";
import InterfazVerSupervisores from "./vistas/UIVerSupervisores";
import InterfazBuscarLider from "./vistas/UIBuscarLider";

function App() {
  const [titulo, setTitulo] = React.useState(null);
  const [mostrarCrearColaborador, setMostrarCrearColaborador] = React.useState(false);

  const [interfazActiva, setInterfazActiva] = React.useState(false);
  const [vista, setVista] = React.useState(0);

  useEffect(() => {
    fetch("/titulo")
      .then((res) => res.json())
      .then((titulo) => setTitulo(titulo.message));
  }, []);



  function desplegarCrearColaborador(){
    console.log("Mostrar crear colaborador");
    setVista(vista != 1 ? 1 : 0);
    setInterfazActiva(vista != 1 ? true : false);

  }

  function desplegarVerColaboradores(){
    console.log("Mostrar ver empleados");
    setVista(vista != 2 ? 2 : 0);  
    setInterfazActiva(vista != 2 ? true : false);

  }

  function desplegarVerSupervisores(){
    console.log("Mostrar ver supervisores");
    setVista(vista != 3 ? 3 : 0);
    setInterfazActiva(vista != 3 ? true : false);

  }

  function desplegarBuscarLider(){
    console.log("Mostrar buscar lider");
    setVista(vista != 4 ? 4 : 0);
    setInterfazActiva(vista != 4 ? true : false);
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>{!titulo ? "Cargando..." : titulo}</p>
      </header>
      <div className="App-subheader">
        <h1>Identificador de colaboradores</h1>
      </div>
      <div className="App-body">
        <div className="body-menu">
        <div onClick={desplegarVerSupervisores}>
        Ver todos los lideres de equipo
        </div>
        <div onClick={desplegarBuscarLider}>
        Buscar lider de equipo
        </div>
        <div onClick={desplegarVerColaboradores}>
        Ver todos los empleados
        </div>
        <div onClick={desplegarCrearColaborador}>
        Crear nuevo colaborador
        </div>
        <div>
        Actualizar colaborador
        </div>
        </div>
        <div className="body-interfaz">
          <a>{ interfazActiva 
                ? vista===1 
                  ? <InterfazCrearColaborador/> 

                  : vista===2
                    ? <InterfazVerColaboradores/>
                    : vista===3
                     ?<InterfazVerSupervisores/>
                     : vista===4
                      ? <InterfazBuscarLider/>
                      : null
                : 'Debe seleccionar una opcion...'}</a>
        </div>
      </div>
    </div>
  );
}

export default App;
