import {useState} from "react";
import "./UICrearColaborador.css";
import colaborador from "../modelos/colaborador.js";
import imgPerfil from "./user_photo.png";
import Dropdown from 'react-bootstrap/Dropdown';

export default function InterfazCrearColaborador() {

    const [nombre, setNombre] = useState('');
    const [nombreVacio, setNombreVacio] = useState(false);
    const [apellido, setApellido] = useState('');
    const [apellidoVacio, setapellidoVacio] = useState(false);
    const [correo, setCorreo] = useState('');
    const [correoVacio, setCorreoVacio] = useState(false);
    const [telefono, setTelefono] = useState('');
    const [fechaNacimiento, setFechaNacimiento] = useState('');
    const [supervisadoPor, setSupervisadoPor] = useState('');
    const [definirSupervisado, setDefinirSupervisado] = useState(false);
    


    function crearColaborador(){
        if( nombre === '' || apellido === '' || correo === ''){
            if(nombre === ''){
                setNombreVacio(true);
            }
            if(apellido === ''){
                setapellidoVacio(true);
            }
            if(correo === ''){
                setCorreoVacio(true);
            }
            return;
        }
        console.log("Crear colaborador");
        let nuevoColaborador = new colaborador(
            nombre,
            apellido,
            correo,
            telefono,
            fechaNacimiento,
            supervisadoPor
        );
        console.log(nuevoColaborador);
        setDefinirSupervisado(true);
    }

    function FormularioCrearColaborador(){
        return(<><div className="form-group">
        <input style={{marginBottom: nombreVacio ? '0' : '2vh'}} type="text" name="nombre" placeholder="Nombre del empleado" 
        onChange={(e) => {
            setNombre(e.target.value);
            setNombreVacio(false);
        }}/>
        {nombreVacio ? <div><a>❌ El nombre no puede estar vacio</a></div> : null}
        <input style={{marginBottom: apellidoVacio ? '0' : '2vh'}} type="text" name="apellido" placeholder="Apellido del empleado"
        onChange={(e) => {
            setApellido(e.target.value);
            setapellidoVacio(false);
        }}/>
        {apellidoVacio ? <div><a>❌ El apellido no puede estar vacio</a></div> : null}
        <input style={{marginBottom: correoVacio ? '0' : '2vh'}} type="text" name="nombre" placeholder="Correo del empleado"
        onChange={(e) => {
            setCorreo(e.target.value);
            setCorreoVacio(false);
        }}/>
        {correoVacio ? <div><a>❌ El correo no puede estar vacio</a></div> : null}
        <input style={{marginBottom: '2vh'}} type="text" name="nombre" placeholder="Telefono del empleado"
        onChange={(e) => {
            setTelefono(e.target.value);
        }}/>
        <input style={{marginBottom: '2vh'}} type="text" name="nombre" placeholder="Fecha de nacimiento"
        onChange={(e) => {
            setFechaNacimiento(e.target.value);
        }}/>

        </div>
        <div className="form-btns">
        <button onClick={crearColaborador}>Crear 🚀</button>
        <button type="reset">Limpiar 💦</button>
        </div>
        </>);
    }

    function PreviewColaborador(){
        return(
            
                <div className="card-colaborador">
                    <div className="info-card">
                    <a>{nombre}</a>
                    <a>{apellido}</a>
                    <a>{correo}</a>
                    <a>{telefono}</a>
                    <a>{fechaNacimiento}</a>
                    </div>
                    <div className="data-card">
                        <div className="div-supervisado">
                            <a>Supervisado por</a>
                            <Dropdown>
                                <Dropdown.Toggle variant="success" id="dropdown-basic">
                                    hola mundo
                                </Dropdown.Toggle>

                            </Dropdown>
                        </div>
                        <div className="div-id">
                            <a>Id</a>
                            </div>
                        
                    </div>
                </div>
            
       
        );
    }

    return (
        <div className="App-Crear-Colaborador">
            
            <a>Formulario para creacion de colaboradores 👨‍💼</a>
            {definirSupervisado ? PreviewColaborador() : FormularioCrearColaborador()}
            
        </div>
    );
}
