import {useState, useRef} from "react";
import "./UICrearColaborador.css";




export default function InterfazBuscarLider() {


    const [supervisor , setSupervisor] = useState(null);
    const [inputVacio , setInputVacio] = useState(true);
    const [buscador , setBuscador] = useState('');


    function cardUF(id,uf,fecha){
        return(<div className="card-UF">
        <div className="info-card">
        <a>{id}</a>
        <a>{uf}</a>
        <a>{fecha}</a>

        </div>
        
    </div>);}

    function buscarSupervisor(){
        fetch('/supervisor/'+buscador)
        .then(response => response.mensaje.json())
        .then(data => {
            
            let lista = [];
            for(let i = 0; i < data.length; i++){
                lista.push(cardUF(data[i].id,data[i].firstname, data[i].lastname, data[i].email, data[i].phone, data[i].birthdate, data[i].supervisedby_id));
            }
            setSupervisor(lista);
            });
        
      }

      const inputRefs = [
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        useRef(null),
        // Agrega tantas referencias como inputs desees controlar
      ];

      function handleInputChange(e, index) {
        const input = e.target;
        if (input.value.length >= input.maxLength && index < inputRefs.length - 1) {
          inputRefs[index + 1].current.focus();
        }
      }

    return (
        <div className="App-Buscar-UF">
            
            <a>Buscar valor de unidad de fomento por fecha ðŸ’¸</a>
            <div style={{marginTop: '5vh',marginBottom: '3vh',padding: '10px'}}>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[0]} onChange={(e) => handleInputChange(e, 0)}></input>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[1]} onChange={(e) => handleInputChange(e, 0)}></input>
                <input className="input-search-uf" placeholder="00" maxLength={2} 
                    ref={inputRefs[2]} onChange={(e) => handleInputChange(e, 0)}></input>
            </div>
            {buscador === "" ? null : <button onClick={buscarSupervisor} className="btn-search-supervisor">Buscar</button>}
            <div style={{
                marginTop: '5vh',width: '100%', display: 'flex',
                justifyContent: 'center', alignItems: 'center'}}>{supervisor}</div>
        </div>
    );
}
