import {useState} from "react";
import "./UICrearColaborador.css";
import colaborador from "../modelos/colaborador.js";
import imgPerfil from "./user_photo.png";
import Dropdown from 'react-bootstrap/Dropdown';



export default function InterfazBuscarLider() {


    const [supervisor , setSupervisor] = useState(null);
    const [inputVacio , setInputVacio] = useState(true);
    const [buscador , setBuscador] = useState('');


    function cardColaborador(id,nombre,apellido,correo,telefono,fecha,supervisado){
        return(<div className="card-colaborador">
        <div className="info-card">
        <a>{nombre}</a>
        <a>{apellido}</a>
        <a>{correo}</a>
        <a>{telefono}</a>
        <a>{fecha}</a>
        </div>
        <div className="data-card">
            <div className="div-supervisado">
                <a>Supervisado por</a>
                <button className="trigger-btn-dropdown">{supervisado === null ? 'Nadie' : supervisado}</button>
            </div>
            <div className="div-id">
                <a>{'Identificador: '+id}</a>
                </div>
            
        </div>
    </div>);}

    function buscarSupervisor(){
        fetch('/supervisor/'+buscador)
        .then(response => response.json())
        .then(data => {
            
            let lista = [];
            for(let i = 0; i < data.length; i++){
                lista.push(cardColaborador(data[i].id,data[i].firstname, data[i].lastname, data[i].email, data[i].phone, data[i].birthdate, data[i].supervisedby_id));
            }
            setSupervisor(lista);
            });
        
      }



    return (
        <div className="App-Crear-Colaborador">
            
            <a>Buscar supervisor en el sistema üë®‚Äçüíª</a>
            <div style={{marginTop: '5vh',marginBottom: '3vh'}}>
                <input onChange={(e)=>setBuscador(e.target.value)} className="input-search-supervisor" placeholder="Identificacion del supervisor..."></input>
            </div>
            {buscador === "" ? null : <button onClick={buscarSupervisor} className="btn-search-supervisor">Buscar</button>}
            <div style={{
                marginTop: '5vh',width: '100%',
                justifyContent: 'center', alignItems: 'center'}}>{supervisor}</div>
        </div>
    );
}
